import React, { useContext, useState} from 'react'
import './CartItems.css';
import { ShopContext } from '../../Contex/ShopContext';

import remove_icon from '../Assets/cart_cross_icon.png'
const CartItems = () => {
    const {getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext)
    const [formState, setFormState] = useState({
        firstName: '',
        lastName: '',
        address: '',
      });
    const handleInputChange = (event) => {
        setFormState({
          ...formState,
          [event.target.name]: event.target.value,
        });
      };
    
      const handleFormSubmit = (event) => {
        event.preventDefault();console.log(formState);
      };
      const [orderPlaced, setOrderPlaced] = useState(false);



const handlePlaceOrder = () => {
  const orderData = {
    firstName: formState.firstName,
    lastName: formState.lastName,
    address: formState.address,
    products: all_product.filter((product) => cartItems[product.id] > 0),
    totalAmount: getTotalCartAmount(),
  };

  axios
    .post('/api/orders', orderData)
    .then((response) => {
      setOrderPlaced(true);
      console.log('Order placed successfully:', response.data);
    })
    .catch((error) => {
      console.error('Error placing order:', error);
    });
};

    return (
        <div className='cartitems'>
            <div className='cartitems-format-main'>
                <p>Product</p>
                <p>Title</p>
                <p>Price</p>
                <p>Quantity</p>
                <p>Total</p>
                <p>remove</p>
            </div>
            <hr />
            <div>

                {all_product.map((e) => {
                    if (cartItems[e.id] > 0) {
                        return <div>
                            <div className='cartitems-format cartitems-format-main'>
                                <img src={e.image} alt="" className='carticon-product-icon' />
                                <p>{e.name}</p>
                                <p>${e.new_price}</p>
                                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
                                <p>{e.new_price*cartItems[e.id]}</p>
                                <img className='cartitems-remove-icon' src={remove_icon} onClick={() => { removeFromCart(e.id) }} alt="" /> {/* Fixed function name here */}
                               </div>
                            <hr />
                        </div>
                    }
                    return null;
                })}
                <div className='cartitems-down'>
                    <div className='cartitems-total'>
                        <h1>cart Totals</h1>
                        <div className='cartitems-total-item'>
                            <p>Subtotal</p>
                            <p>${getTotalCartAmount()}</p>

                        </div>
                        <hr />
                        <div className='cartitems-total-item '>
                            <p>Shipping</p>
                            <p>Free Shipping on orders over $99.</p>
                        </div>
                        <hr />
                        <div className='cartitems-total-item'>
                            <h3>total</h3>
                            <h3>&{getTotalCartAmount()}</h3>
                        </div>
                        <button>Checkout</button>
                    </div >
                    <div className="cartitems-form1">
                        <h3>fill the details</h3>
                        {!orderPlaced && (
      <form className="cartitems-form" onSubmit={handleFormSubmit}>
        <label htmlFor="firstName">First name:</label><br />
        <input type="text" id="firstName" name="firstName" value={formState.firstName} onChange={handleInputChange} required /><br />
        <label htmlFor="lastName">Last name:</label><br />
        <input type="text" id="lastName" name="lastName" value={formState.lastName} onChange={handleInputChange} required /><br />
        <label htmlFor="address">Address:</label><br />
        <input type="text" id="address" name="address" value={formState.address} onChange={handleInputChange} required /><br />
        <button type="submit">Place Order</button>
      </form>
    )}
    {orderPlaced && <p>Order placed successfully!</p>}
  </div>
                </div>
            </div>


        </div>
    )
}

export default CartItems
